"use client"

import * as React from "react"

import { Card, CardContent } from "@/components/ui/card"
import { Steps } from "@/registry/default/annui/steps"

const StepsCard = React.forwardRef<
  React.ComponentRef<typeof Steps>,
  React.ComponentPropsWithoutRef<typeof Steps>
>(({ children, ...props }, ref) => {
  return (
    <Steps asChild ref={ref} {...props}>
      <Card className="flex flex-col gap-4 p-0">{children}</Card>
    </Steps>
  )
})
StepsCard.displayName = "StepsCard"

const StepsCardContent = React.forwardRef<
  React.ComponentRef<typeof CardContent>,
  React.ComponentPropsWithoutRef<typeof CardContent>
>(({ children, ...props }, ref) => {
  return (
    <CardContent ref={ref} {...props}>
      {children}
    </CardContent>
  )
})
StepsCardContent.displayName = "StepsCardContent"

const StepsCardFooter = React.forwardRef<
  React.ComponentRef<typeof Card>,
  React.ComponentPropsWithoutRef<typeof Card>
>(({ children, ...props }, ref) => {
  return (
    <Card ref={ref} {...props}>
      {children}
    </Card>
  )
})
StepsCardFooter.displayName = "StepsCardFooter"

export { StepsCard, StepsCardContent, StepsCardFooter }
